	 */
	public function update($id)
	{

		$data = Input::all();

        try {
            $record = $this->domainService->updateDomain($id, $data);

            // @todo: Redirect to proper URL
            Session::flash('message', 'Successfully updated!');
            
            return $this->redirectAfterPost(
